function[lny,sigma,tau,sig]=Bernal2014(To,M,rrup,H,mechanism)

if  To<0 || To> 8
    lny   = nan(size(M));
    sigma = nan(size(M));
    tau   = nan(size(M));
    sig   = nan(size(M));
    %IM    = IM2str(To);
    %h=warndlg(sprintf('GMPE %s not available for %s',mfilename,IM{1}));
    %uiwait(h);
    return
end

To      = max(To,0.001); %PGA is associated to To=0.001;
period  = [0.001 0.05 0.1 0.15 0.3 0.5 1 1.5 2 2.5 3 7 8];
T_lo    = max(period(period<=To));
T_hi    = min(period(period>=To));
index   = find(abs((period - T_lo)) < 1e-6); % Identify the period

if T_lo==T_hi
    [lny,sigma] = gmpe(index,M,rrup,H,mechanism);
else
    [lny_lo,sigma_lo] = gmpe(index,  M,rrup,H,mechanism);
    [lny_hi,sigma_hi] = gmpe(index+1,M,rrup,H,mechanism);
    x          = log([T_lo;T_hi]);
    Y_sa       = [lny_lo,lny_hi]';
    Y_sigma    = [sigma_lo,sigma_hi]';
    lny        = interp1(x,Y_sa,log(To))';
    sigma      = interp1(x,Y_sigma,log(To))';
end

tau=0*sigma;
sig=sigma;

